class Solution {

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        std::unordered_set<int> done_pos;
        std::vector<std::unordered_set<int>> pos_to_sums(nums.size());
        for (int i = 0; i < nums.size(); ++i) {
            pos_to_sums[i].insert(0);
            if (nums[i] == 0) {
                done_pos.insert(i);
            }
        }

        int result = 0;
        for (const auto& q: queries) {
            if (done_pos.size() == nums.size()) {
                break;
            }
            result++;
            int l = q[0];
            int r = q[1];
            int delta = q[2];
            for (int pos = l; pos <= r; ++pos) {
                std::unordered_set<int> sums = pos_to_sums[pos];
                for (int sum: sums) {
                    int candidate = sum + delta;
                    pos_to_sums[pos].insert(candidate);
                    if (candidate == nums[pos]) {
                        done_pos.insert(pos);
                    }
                }
            }
        }
        if (done_pos.size() == nums.size()) {
            return result;
        }
        return -1;
    }
};

