using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        map<int, vector<int>> mp;
        int n = v.size();
        for (int i = 0; i < q.size(); i++) {
            for (int j = q[i][0]; j <= q[i][1]; j++) {
                mp[j].push_back(i);
            }
        }
        for(int i=0;i<n;i++){
            if(mp.find(i)==mp.end() && v[i]!=0)return -1;
        }

        auto fn = [&](auto&& self, int i, int sum, vector<int>& vv, vector<vector<int>>& dp, int val) -> int {
            if (i >= vv.size() || sum>val) return 1e8;
            if (dp[i][sum] != -1) return dp[i][sum];
            int newsum = q[vv[i]][2]+sum;
            if(newsum == val){
                return dp[i][sum] = vv[i]+1;
            }
            return dp[i][sum] = min(self(self, i + 1, sum, vv, dp, val), 
                                    self(self, i + 1, newsum, vv, dp, val));
        };

        int ans = 0;
        for (auto& [x, y] : mp) {
            if(v[x]==0)continue;
            vector<vector<int>> dp(y.size() + 1, vector<int>(1001, -1));
            ans = max(ans, fn(fn, 0, 0, y, dp, v[x]));
            cout<<ans<<endl;
            if (ans > 1e7) return -1;
        }

        return ans;
    }
};

