               if(qu[j][0]<=i&&i<=qu[j][1]){
                   dp[i].push_back({qu[j][2],j});
               }
           }
        }
      //  vector<int>res(nu.size());
        int ans=0;
        for(int i=0;i<nu.size();i++){
            int uy=tree(nu[i],dp[i]);
            if(uy==-5){
                return -1;
            }
            ans=max(ans,uy+1);
        }
        return ans;
    }
};

