#include <vector>
#include <bitset>
#include <algorithm>

using namespace std;

class Solution {
public:
    bool canMakeZero(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);
        for (int i = 0; i < n; i++) {
            dp[i].reset();
            dp[i].set(0);
        }
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; j++) {
                dp[j] |= (dp[j] << v);
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].reset(pos);
                }
            }
        }

        for (int i = 0; i < n; i++) {
            if (!dp[i].test(nums[i])) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size(), ans = -1;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (canMakeZero(nums, queries, mid)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

