#include <bits/stdc++.h>
using namespace std;

class Solution {
    public:
    vector<vector<pair<int, int>>> cover;


    bool func(int mid, vector<int>& nums) {
        int n = nums.size();
        for (int pos = 0; pos < n; ++pos) {
            int target = nums[pos];
            if (target == 0) continue;

            auto it = lower_bound(cover[pos].begin(), cover[pos].end(), make_pair(mid, 0));
            vector<int> arr;
            for (auto iter = cover[pos].begin(); iter != it;iter++) {
                if (iter->second <= target) {
                    arr.push_back(iter->second);
                }
            }

            vector<int> dp(target + 1, 0);
            dp[0] = 1;

            for (int d : arr) {
                for (int j = target; j >= d; --j) {
                    dp[j] = dp[j] || dp[j - d];  
                }
            }

            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        cover.resize(n);

        for (int i = 0; i < m; ++i) {
            int l = queries[i][0], r = queries[i][1], dec = queries[i][2];
            for (int pos = l; pos <= r; ++pos) {
                cover[pos].emplace_back(i, dec);
            }
        }

        for (int i = 0; i < n; ++i) {
            sort(cover[i].begin(), cover[i].end());
        }

        int lo = 0, hi = m + 1;
        while (lo < hi) {
            int mid = (lo + hi) / 2;
            if (func(mid,nums)) {
                hi = mid;
            } else {
                lo = mid + 1;
            }
        }

        return (lo <= m) ? lo : -1;
    }
};

