class Solution {
public:
    int minZeroArray(vector<int>& A, vector<vector<int>>& queries) {
        int ans = -1;
        int l = 0;
        int r = queries.size() - 1;
        int n = A.size();
        bool allZero = true;
        for (auto& c : A) {
            if (c != 0) {
                allZero = false;
            }
        }
        if (allZero) return 0;

        auto check = [&](int m) -> bool {
            vector<vector<int>> pos(n);
            for (int i = 0; i <= m; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];
                for (int v = l; v <= r; v++) {
                    pos[v].push_back(val);
                }
            }
            bool ok = true;
            for (int i = 0; i < n && ok; i++) if (A[i] != 0) {
                vector<bool> dp(A[i] + 1,false);
                dp[0] = true;
                for (auto& c : pos[i]) {
                    for (int v = A[i]; v >= c; v--) if (dp[v - c]) {
                        dp[v] = true;
                    }
                }
                ok &= dp[A[i]];
            }
            return ok;
        };
        while (l <= r) {
            int m = (l + r) / 2;
            if (check(m)) {
                ans = m + 1;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return ans;
    }
};

