from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        max_k = 0
        for i in range(n):
            required = nums[i]
            if required == 0:
                continue  # contributes 0 to max_k
            q_list = []
            for pos, (l, r, val) in enumerate(queries):
                if l <= i <= r:
                    q_list.append((val, pos))
            if not q_list:
                return -1
            dp = {0: 0}
            for val, pos in q_list:
                new_dp = {}
                for s in dp:
                    # Option 1: do not take this query
                    if s not in new_dp or dp[s] < new_dp.get(s, float('inf')):
                        new_dp[s] = min(new_dp.get(s, float('inf')), dp[s])
                    # Option 2: take this query
                    new_s = s + val
                    new_k = max(dp[s], pos + 1)
                    if new_s not in new_dp or new_k < new_dp.get(new_s, float('inf')):
                        new_dp[new_s] = min(new_dp.get(new_s, float('inf')), new_k)
                dp = new_dp
            if required not in dp:
                return -1
            current_k = dp[required]
            if current_k > max_k:
                max_k = current_k
        return max_k if max_k != 0 or all(num == 0 for num in nums) else 0

