class Solution {
public:
    int minZeroArray(vector<int>& elements, vector<vector<int>>& operations) {

    int n=elements.size();
        vector<int>v;
        for(auto &p:elements){
            v.push_back(p);
        }

        int size = elements.size(), numOps = operations.size();

        auto isZeroPossible = [&](int k) -> bool {
            for (int j = 0; j < size; j++) {
                bitset<1001> dp;
                dp[0] = true;

                for (int i = 0; i < k; i++) {
                    if (j >= operations[i][0] && j <= operations[i][1]) {
                        int value = operations[i][2];
                        for (int s = 1000; s >= value; s--) {
                            if (dp[s - value]) dp[s] = true;
                        }
                    }
                }

                if (!dp[elements[j]]) return false;
            }
            return true;
        };
        unordered_map<int,int>m;
        for(auto &p:v){
            m[p]++;
        }
        int left = 0, right = numOps;
        int minOps = -1;

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (isZeroPossible(mid)) {
                minOps = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        return minOps;
    }
};

