        n = len(nums)
        if all(num == 0 for num in nums):
            return 0
        cache = [set() for _ in range(n)]
        for j in range(n):
            cache[j].add(0)

        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                lst = {x + v for x in cache[j]}
                cache[j].update(lst)
                for posi in range(nums[j] + 1, 1001):
                    if posi in cache[j]:
                        cache[j].remove(posi)

            if all(any(x == num for x in cache[j]) for j, num in enumerate(nums)):
                return k + 1

        return -1

