        n = len(nums)
        HARIVEL = (nums, queries)  # Equivalent to the C++ pair

        dp = [set() for _ in range(n)]
        for j in range(n):
            dp[j].add(0)

        if all(num == 0 for num in nums):
            return 0

        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                new_values = {x + v for x in dp[j] if x + v <= 1000}
                dp[j].update(new_values)

                dp[j] = {x for x in dp[j] if x <= nums[j]}  # Limit values to nums[j]

            if all(nums[j] in dp[j] for j in range(n)):
                return k + 1

        return -1

