class Solution {
public:
    #define pi pair<int,int>
    #define fi first
    #define sd second

    int dp[1001][1001];

    int f(vector<pi>& v,int x,int i,int n) {

        if(x == 0) {
            return 0;
        }

        if(i == n || x<0) {
            return 1e9;
        }

        if(v[i].fi == x) {
            return v[i].sd;
        }

        if(dp[x][i] != -1) {
            return dp[x][i];
        }

        int a = f(v,x,i+1,n);
        int b = f(v,x - v[i].fi,i+1,n);

        return dp[x][i] = min(a,b);
    }

    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        int n = v.size();
        vector<vector<pi>> mp(n);
        int i,qn=q.size(),j;

        for(i=0;i<qn;i++) {

            int l = q[i][0];
            int r = q[i][1];
            int x = q[i][2];

            for(j=l;j<=r;j++) {
                mp[j].push_back({x,i+1});
            }
        }
        int ans = 0;

        for(i=0;i<n;i++) {
            memset(dp, -1, sizeof(dp));
            ans = max(ans, f(mp[i], v[i], 0, mp[i].size()));
        }
        return ans == 1e9 ? -1 : ans;
    }
};

