class Solution {
public:
    int dp[1001][1001];
    int f(int i,int val,vector<int>&comb){
        if(val == 0) return 1;
        if(i == comb.size()) return 0;
        if(dp[i][val] != -1) return dp[i][val];
        bool ans = false;
        if(comb[i] <= val) ans |= f(i + 1,val - comb[i],comb);
        ans |= f(i + 1,val,comb);
        return dp[i][val] = ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>&queries) {
        int low = 0,high = queries.size(),ans = -1;
        int n = nums.size();

        auto ok = [&](int mid)->bool{
            vector<vector<int>>comb(n);
            for(int i = 0;i < mid; i++){
                int l = queries[i][0],r = queries[i][1],val = queries[i][2];
                while(l <= r){
                    comb[l].push_back(val);
                    l++;
                }
            }
            // what i have 10 -> vals, total 1e3
            // check whether it is possible to make with this combinations
            for(int i = 0;i < n; i++){
                for(int j = 0 ;j < comb[i].size(); j++){
                    for(int k = 0; k < nums[i] + 1; k++){
                        dp[j][k] = -1;
                    }
                }
                if(!f(0,nums[i],comb[i])){
                     return false;
                }
            }
            comb.clear();
            return true;
        };
        while(low <= high){
            int mid = (low + high)/2;
            if(ok(mid)){
                high = mid - 1;
                ans = mid;
            }else{
                low = mid + 1;
            }
        }
        return ans;
    }
};

