class Solution {
    public int minZeroArray(int[] nums, int[][] query) {
        int temp;
        int ans=0;
        int max=0;
        for(int e:nums){
            max=Math.max(max,e);
        }
        dp=new int[max+1][query.length][11];
        for(int d[][]:dp){
            for(int d1[]:d){
                Arrays.fill(d1,-1);
            }
        }
        for(int i=0;i<nums.length;i++){
            temp=helper(query,nums[i],0,i);
            if(temp==Integer.MAX_VALUE)
                return -1;
            // System.out.println("i="+i+" nm="+nums[i]+" temp="+temp);
            ans=Math.max(ans,temp);
            // reset(dp);
        }
        return ans;
    }
    int dp[][][];
    public int helper(int query[][],int targ,int idx,int numidx){
        if(targ==0)
        return idx;

        if(idx==query.length)
        return Integer.MAX_VALUE;

        if(dp[targ][idx][numidx]!=-1)
        {
            // System.out.println("backtrack");
            return dp[targ][idx][numidx];
        }

        int ans=Integer.MAX_VALUE;
        if(query[idx][0]<=numidx&&numidx<=query[idx][1]&&targ>=query[idx][2]){
            ans=Math.min(ans,helper(query,targ-query[idx][2],idx+1,numidx));
        }
        ans=Math.min(ans,helper(query,targ-0,idx+1,numidx));    

        dp[targ][idx][numidx]=ans;

        return ans;
    }
}

