class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // foreach indices i in [0, n]                        O(n)
        //    for k in filtered-queries                       O(q)
        //        filter queries involve i                    
        //        do combine or not?                          O(v)
        //        check if nums[i] in dynamic set

        const int m = queries.size();
        const int n = nums.size();
        unordered_set<int> curr, next;
        int l, r, v;
        int i, k;
        int ans = 0;

        for (i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                continue;
            }

            curr.clear(); curr.insert(0);
            next.clear();

            for (k = 0; k < m; ++k) {
                l = queries[k][0];
                r = queries[k][1];
                v = queries[k][2];

                if (i < l || i > r)
                    continue;

                for (const auto e: curr) {
                    next.insert(e);
                    next.insert(e + v);
                }

                swap(curr, next);
                if (curr.count(nums[i])) {
                    break;
                }
            } // k

            ans = max(ans, k + 1);
            if (k == m)
                return -1;
        }

        return ans;
    }
};

