class Solution {
public:

bool canFormX(vector<int>& a, int x) {
    vector<int> dp(x + 1, false);
    dp[0] = true;

    for (int num : a) {
        for (int j = x; j >= num; j--) {
            dp[j] |= dp[j - num];
        }
    }

    return dp[x];
}

int minStepsToX(vector<int>& a, int x) {
    unordered_set<int> visited;
    queue<pair<int, int>> q;  // (current state, steps)
    q.push({0, 0});
    visited.insert(0);

    while (!q.empty()) {
        auto [cur, steps] = q.front();
        q.pop();
        if (cur == x) return steps;

        for (int num : a) {
            int newState = cur + num;
            if (newState <= x && !visited.count(newState)) {
                visited.insert(newState);
                q.push({newState, steps + 1});
            }
        }
    }
    return -1;  // If not possible
}

bool check(int k,vector<int>&a,vector<vector<int>>&queries){
    int n=a.size(),q=queries.size();
    vector<vector<int>> setq(queries.begin(),queries.begin()+k);
    vector<vector<int>> mp(n);
    // sort(setq.begin(),setq.end(),comp);
    for(int i=0;i<k;i++){
        int l=setq[i][0],r=setq[i][1],val=setq[i][2];
        for(int j=l;j<=r;j++){
            mp[j].push_back(val);
        }
    }
    for(int i=0;i<n;i++){
        if(a[i]==0) continue;
        else{
            bool ch=canFormX(mp[i],a[i]);
            if(!ch){
                return false;
            }

        }
    }
    return true;
}


int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
    int n=a.size(),q=queries.size(),ans=-1;
    int lo=0,hi=q;
    while(lo<=hi){
        int mid=(lo+hi)/2;
        if(check(mid,a,queries)){
            hi=mid-1;
            ans=mid;
        }
        else{
            lo=mid+1;
        }
    }
    return ans;
}
};

