class Solution {
public int minZeroArray(int[] nums, int[][] queries) {
    int n = nums.length;
    boolean allZero = true;
    for (int x : nums) {
        if (x != 0) {
            allZero = false;
        }
    }
    if (allZero) return 0;

    int Q = queries.length;
    boolean[][] possible = new boolean[n][];
    for (int i = 0; i < n; i++) {
        possible[i] = new boolean[nums[i] + 1];
        possible[i][0] = true;
    }

    for (int j = 0; j < Q; j++) {
        int l = queries[j][0];
        int r = queries[j][1];
        int val = queries[j][2];

        for (int i = l; i <= r; i++) {
            if (nums[i] >= val) {
                for (int s = nums[i]; s >= val; s--) {
                    if (possible[i][s - val]) {
                        possible[i][s] = true;
                    }
                }
            }
        }
        boolean canMakeAll = true;
        for (int i = 0; i < n; i++) {
            if (!possible[i][nums[i]]) {
                canMakeAll = false;
                break;
            }
        }
        if (canMakeAll) {
            return j + 1;
        }
    }
    return -1;
}

}

