class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        unordered_map<int,set<int>>indexToHit;
        vector<bool> resolved(nums.size(), false);
        for(int i = 0; i < nums.size(); i++){
            indexToHit[i].insert(nums[i]);
            if(nums[i]==0){
                resolved[i] = true;
            }
        }

        bool alrSolved = true;
        for(bool s : resolved){
            alrSolved = alrSolved && s;
        }
        if(alrSolved){
            return 0;
        }

        for(int i = 0; i < queries.size(); i++){
            vector<int> cur = queries[i];
            int val = cur[2];
            bool solved = true;
            for(int index = cur[0]; index <= cur[1]; index++){
                if(!resolved[index]){
                    set<int> curTargets = indexToHit[index];
                    for(int target : curTargets){
                        if(target == val){
                            resolved[index] = true;
                        }
                    }
                    if(!resolved[index]){
                        set<int> newTargets;
                        for(int i : curTargets){
                            newTargets.insert(i);
                            if(i-val > 0){
                                newTargets.insert(i-val);
                            }
                        }
                        indexToHit[index] = newTargets;
                    }
                }
            }
            for(bool s : resolved){
                solved = solved && s;
            }
            if(solved){
                return i+1;
            }
        } 

        return -1;
    }
};

