class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        best_k = -1
        for ind in range(len(nums)):
            num = nums[ind]
            if num == 0:
                best_k = max(0, best_k)
                continue
            contain = [False] * (num + 1)
            contain[0] = True
            for k in range(len(queries)):
                l, r, val = queries[k]
                if l <= ind <= r:
                    for i in range(num - val, -1, -1):
                        if not contain[i]: continue
                        if contain[i]:
                            contain[i + val] = True
                if contain[-1]:
                    best_k = max(best_k, k + 1)
                    break
            if not contain[-1]:
                return -1
        return best_k

