class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<bool> bZero(n, false);
        int cntZero = 0;
        vector<set<int>> vecs(n);
        for(int i = 0; i < n; i++){
            if(nums[i] == 0){
                bZero[i] = true;
                cntZero++;
                continue;
            }
            vecs[i].insert(nums[i]);
        }
        if(cntZero == n)
            return 0;
        for(int k = 0; k < q; k++){
            int left = queries[k][0];
            int right = queries[k][1];
            int val = queries[k][2];
            for(int i = left; i <= right; i++){
                if(bZero[i])
                    continue;
                for(auto& v : vecs[i]){
                    if(v == val){
                        bZero[i] = true;
                        cntZero++;
                        break;
                    }
                    if(v - val > 0)
                        vecs[i].insert(v - val);
                }
            }
            if(cntZero == n)
                return k + 1;
        }
        return -1;
    }
};

