class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        ans = -1
        # row represent index (MAX 10)
        # col represent querose (MAX 1000)
        # grid[i][j] is does ith index is in range of queries[j]
        grid = [[0]*m for i in range(n)]

        for i in range(n):
            for j in range(m):
                l, r, v = queries[j]
                if l<=i<=r:
                    grid[i][j] = v

        # for i in grid:
        #     print(i)

        INT_MAX = 1000000000000
        for row in range(n):
            # for each index find minimum queries from start to make it zero

            @cache
            def dp(i, curr):
                if curr == 0:
                    return i

                if curr < 0:
                    return INT_MAX

                if i >= m:
                    return INT_MAX

                return min(dp(i+1, curr), dp(i+1, curr-grid[row][i]))

            temp = dp(0, nums[row])
            # print(temp)
            ans = max(ans, temp)

        return ans if ans != INT_MAX else -1

