        def poss(mid):
            for i in range(len(nums)):
                bit=1
                for l,r,v in queries[:mid]:
                    if l<=i<=r:
                        bit|=bit<<v
                if not bit&(1<<nums[i]):
                    return False
            return True
        l=0
        r=len(queries)
        ans=False
        while l<=r:
            mid=l+(r-l)//2
            if poss(mid):
                ans=True
                r=mid-1
            else:
                l=mid+1
        return l if ans else -1

