class Solution(object):
    def subtract(self, s, val):
        if val in s:
            return True
        t = []
        for i in s:
            d = i - val
            if d > 0 and d not in s:
                t.append(d)
        for i in t:
            s.add(i)
        return False


    def minZeroArray(self, nums, queries):
        """
        :type nums: List[int]
        :type queries: List[List[int]]
        :rtype: int
        """
        n = len(nums)
        sets = []
        res = 0
        left = n
        for i in nums:
            if i == 0:
                left -= 1
                sets.append([None, True])
            else:
                s = set()
                s.add(i)
                sets.append([s, False])
        if left == 0:
            return res

        for l, r, val in queries:
            res += 1
            for i in range(l, r+1):
                if not sets[i][1]:
                    sets[i][1] = self.subtract(sets[i][0], val)
                    left -= sets[i][1]
            if left == 0:
                return res
        return -1

