class Solution {
    List<Integer> list;
    int end;
    Boolean[][] dp;
    public int minZeroArray(int[] nums, int[][] queries) {
        List<List<Integer>> index2List = new ArrayList<>();
        boolean allZero = true;
        for (int i = 0; i < nums.length; i++) {
            List<Integer> l = new ArrayList<>();
            for (int[] q : queries) {
                if (q[0] <= i && q[1] >= i) {
                    l.add(q[2]);
                } else {
                    l.add(0);
                }
            }        
            index2List.add(l);
            if (nums[i] != 0) {
                allZero = false;
            }
        }
        if (allZero) {
            return 0;
        }

        int left = 0;
        int right = queries.length - 1;
        while (left < right) {
            int mid = left + (right - left) / 2;
            end = mid;
            boolean valid = true;
            for (int i = 0; i < nums.length; i++) {
                list = index2List.get(i);
                dp = new Boolean[nums[i] + 1][end + 1];
                if (!dfs(nums[i], 0)) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }

        boolean valid = true;
        end = left;
        for (int i = 0; i < nums.length; i++) {
            dp = new Boolean[nums[i] + 1][end + 1];
            list = index2List.get(i);
            if (!dfs(nums[i], 0)) {
                valid = false;
                break;
            }
        }
        if (valid) {
            return left + 1;
        }
        return -1;
    }

    private boolean dfs(int sum, int index) {
        if (sum == 0) {
            return true;
        }
        if (sum < 0) {
            return false;
        }
        if (index > end) {
            return false;
        }
        if (dp[sum][index] != null) {
            return dp[sum][index];
        }
        boolean res = dfs(sum, index + 1) || dfs(sum - list.get(index), index + 1);
        return dp[sum][index] = res;
    }
}

