class Solution {
public:
    bool checker(int A, const vector<int> & nums){
        vector<bool>dp(A+1, false);
        dp[0]=true;
        for(int num:nums){
            if(num>A){continue;}
            for(int j=A;j>=num;--j){
                dp[j]=dp[j] || dp[j-num];
            }
        }return dp[A];
    }

    bool solver(vector<int>& nums, vector<vector<int>>& q, int mid){
        bool final=true;

        for(int i=0;i<nums.size();i++){
            //filter query values for this index
            vector<int> qur;
            for(int j=0;j<=mid;j++){
                vector<int> qi=q[j];
                if(qi[0]<=i && qi[1]>=i){qur.push_back(qi[2]);}
            }
            if(checker(nums[i], qur)){
                continue;
            }else{
                return false;
            }
        }return true;
    }


    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        if(q.empty()){return -1;}
        //check all 0
        bool temp_zero=true;
        for(auto k:nums){
            if(k!=0){
                temp_zero=false;
            }
        }if(temp_zero==true){return 0;}

        int i=0, j=q.size()-1;
        int ans=INT_MAX;
        while(i<=j){
            int mid=(i+j)/2;
            if(solver(nums, q, mid)){
                ans=min(ans, mid);
                j=mid-1;
            }else{
                i=mid+1;
            }
        }
        if(ans==INT_MAX){return -1;}
        return ans+1;
    }
};

