        for i in range(len(nums)):
            if nums[i] == 0: continue
            S = set()
            S.add(nums[i])
            for j in range(1, len(queries)+1):
                if not queries[j-1][0] <= i <= queries[j-1][1]:
                    continue
                T = set()
                for n in S:
                    dif = n - queries[j-1][2]
                    if dif > 0:
                        T.add(dif)
                    elif dif == 0:
                        res = max(res, j)
                        break
                else:
                    S |= T
                    continue
                break
            else:
                return -1
        return res

