public class Solution {
    public int MinZeroArray(int[] nums, int[][] queries) 
    {
        int Q = queries.Length;
        int N = nums.Length;
        int low = 0, high = Q + 1; 

        while (low < high)
        {
            int mid = low + (high - low) / 2;
            if (CanReduce(nums, queries, mid)) high = mid;
            else low = mid + 1;
        }

        return low <= Q ? low : -1;
    }

    private bool CanReduce(int[] nums, int[][] queries, int k)
        {
            int N = nums.Length;
            // all query values that change index
            List<int>[] change = new List<int>[N];
            for (int j = 0; j < N; j++)
            {
                change[j] = new List<int>();
            }
            // first k queries
            for (int i = 0; i < k; i++)
            {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for (int j = l; j <= r; j++)
                {
                    change[j].Add(val);
                }
            }
            // can we form nums[j] from the queries 
            for (int j = 0; j < N; j++)
            {
                if (!DPSubsetSum(change[j], nums[j]))
                    return false;
            }
            return true;
        }

    private bool DPSubsetSum(List<int> vals, int target)
        {
            if (target == 0) return true; // luck

            // if the sum of all available values is less than target
            int sum = 0;
            foreach (int v in vals)
                sum += v;
            if (sum < target) return false;

            // DP is true if sum s can be formed
            bool[] dp = new bool[target + 1];
            dp[0] = true;
            foreach (int v in vals)
            {
                for (int s = target; s >= v; s--)
                {
                    if (dp[s - v])
                        dp[s] = true;
                }
            }
            return dp[target];
        }
}

