using namespace IO;

class SegmentTree {
private:
    int n;
    vector<ll> tree, lazy;

    void push(int node, int l, int r) {
        if (lazy[node] != 0) {
            tree[node] += lazy[node];
            if (l != r) {
                lazy[node * 2]     += lazy[node];
                lazy[node * 2 + 1] += lazy[node];
            }
            lazy[node] = 0;
        }
    }

    void range_add(int node, int l, int r, int ql, int qr, int val) {
        push(node, l, r);
        if (qr < l || r < ql) {
            return;
        }
        if (ql <= l && r <= qr) {
            lazy[node] += val;
            push(node, l, r);
            return;
        }
        int mid = (l + r) >> 1;
        range_add(node * 2, l, mid, ql, qr, val);
        range_add(node * 2 + 1, mid + 1, r, ql, qr, val);
        tree[node] = max(tree[node * 2], tree[node * 2 + 1]);
    }

    ll range_max(int node, int l, int r, int ql, int qr) {
        push(node, l, r);
        if (qr < l || r < ql) {
            return numeric_limits<int>::lowest();
        }
        if (ql <= l && r <= qr) {
            return tree[node];
        }
        int mid = (l + r) >> 1;
        return max(range_max(node * 2, l, mid, ql, qr), range_max(node * 2 + 1, mid + 1, r, ql, qr));
    }

    ll range_min(int node, int l, int r, int ql, int qr) {
        push(node, l, r);
        if (qr < l || r < ql) {
            return (ll) 1e18;
        }
        if (ql <= l && r <= qr) {
            return tree[node];
        }
        int mid = (l + r) >> 1;
        return min(range_min(node * 2, l, mid, ql, qr), range_min(node * 2 + 1, mid + 1, r, ql, qr));
    }

    void build(int node, int l, int r, const vector<int>& data) {
        if (l == r) {
            tree[node] = data[l];
            return;
        }
        int mid = (l + r) >> 1;
        build(node * 2, l, mid, data);
        build(node * 2 + 1, mid + 1, r, data);
        tree[node] = max(tree[node * 2], tree[node * 2 + 1]);
    }

public:

    SegmentTree(const vector<int>& data) {
        n = (int) data.size();
        tree.resize(4 * n);
        lazy.assign(4 * n, 0);
        build(1, 0, n - 1, data);
    }

    void range_add(int l, int r, int val) {
        range_add(1, 0, n - 1, l, r, val);
    }

    ll range_max(int l, int r) {
        return range_max(1, 0, n - 1, l, r);
    }

    ll range_min(int l, int r) {
        return range_min(1, 0, n - 1, l, r);
    }
};

class Solution {
public:
    int dp[1005][11];
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        memset(dp, 0, sizeof dp);
        for (int i = 0; i < n; i++) {
            dp[0][i] = 1;
        }

        int f = 1;
        for (int i = 0; i < n && f; i++) {
            if (nums[i] != 0) {
                f = false;
            }
        }

        if (f) {
            return 0;
        }

        for (int i = 0; i < q; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];

            for (int j = l; j < r + 1; j++) {
                for (int s = nums[j]; s >= v; s--) {
                    if (!dp[s][j] && dp[s - v][j]) dp[s][j] = 1;
                }
            }

            bool out = 1;
            for (int ans = 0; ans < n && out; ans++) {
                if (dp[nums[ans]][ans] == 0) {
                    out = 0;
                }
            }

            if (out) {
                return i + 1;
            }
        }
        return -1;
    }
};

