class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        boolean isZero = true;
        for (int num : nums) {
            if (num != 0) {
                isZero = false;
                break;
            }
        }
        if (isZero) return 0;

        boolean[][] dp = new boolean[n][];
        for (int i = 0; i < n; i++) {
            dp[i] = new boolean[nums[i] + 1];
            dp[i][0] = true;
        }

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];
            for (int i = l; i <= r && i < n; i++) {
                for (int s = nums[i] - val; s >= 0; s--) {
                    if (dp[i][s]) {
                        dp[i][s + val] = true;
                    }
                }
            }
            isZero = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    isZero = false;
                    break;
                }
            }
            if (isZero) return k + 1;
        }

        return -1;
    }
}

