public class Solution {
     private boolean isAllZero(int[] nums) {
        for (int num : nums) {
            if (num != 0) {
                return false;
            }
        }
        return true;
    }

    private static final int MAX_SIZE = 1001;

    private BitSet shiftLeft(BitSet bs, int shift) {
        BitSet shifted = new BitSet(MAX_SIZE);
        for (int i = 0; i < MAX_SIZE - shift; i++) {
            if (bs.get(i)) {
                shifted.set(i + shift);
            }
        }
        return shifted;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<BitSet> dp = new ArrayList<>(n);

        for (int i = 0; i < n; i++) {
            BitSet bitSet = new BitSet(MAX_SIZE);
            bitSet.set(0, true);
            dp.add(bitSet);
        }

        if (isAllZero(nums)) {
            return 0;
        }

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                dp.get(j).or(shiftLeft(dp.get(j), v));

                for (int pos = nums[j] + 1; pos < MAX_SIZE; pos++) {
                    dp.get(j).clear(pos);
                }
            }
            if (allAchieved(nums, dp)) {
                return k + 1;
            }
        }

        return -1;
    }

    private boolean allAchieved(int[] nums, List<BitSet> dp) {
        for (int i = 0; i < nums.length; i++) {
            if (!dp.get(i).get(nums[i])) {
                return false;
            }
        }
        return true;
    }
}

