import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<BitSet> dp = new ArrayList<>(n);

        for (int j = 0; j < n; j++) {
            dp.add(new BitSet(1001));
            dp.get(j).set(0, true);
