            }
        }

        if (allZero) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet shifted = (BitSet) dp.get(j).clone();
                shifted = shifted.get(0, 1001);
                shifted = shiftLeft(shifted, v);
                dp.get(j).or(shifted);

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp.get(j).clear(pos);
                }
            }

            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp.get(j).get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }

    private BitSet shiftLeft(BitSet bitSet, int shift) {
        BitSet shifted = new BitSet(1001);
        for (int i = 0; i < 1001 - shift; i++) {
            if (bitSet.get(i)) {
                shifted.set(i + shift);
            }
        }
        return shifted;
    }
}

