        for (int j = 0; j < n; j++) {
            dp.add(new BitSet(1001));
            dp.get(j).set(0);
        }

        boolean zeros = true;
        for (int num : nums) {
            if (num != 0) {
                zeros = false;
                break;
            }
        }
        if (zeros) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet temp = (BitSet) dp.get(j).clone();
                temp.or(shiftLeft(dp.get(j), v));

                dp.set(j, temp);
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp.get(j).clear(pos);
                }
            }

            boolean all = true;
            for (int j = 0; j < n; j++) {
                if (!dp.get(j).get(nums[j])) {
                    all = false;
                    break;
                }
            }
            if (all) return k + 1;
        }

        return -1;
    }

    private BitSet shiftLeft(BitSet bits, int shift) {
        BitSet result = new BitSet(1001);
        for (int i = bits.nextSetBit(0); i >= 0; i = bits.nextSetBit(i + 1)) {
            if (i + shift < 1001) {
                result.set(i + shift);
            }
        }
        return result;
    }
}

