#include <bitset>
class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int lo = 0, hi = q + 1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            bool valid = true;
            for (int i = 0; i < n; i++) {
                bitset<1001> dp;
                dp.reset();
                dp[0] = 1;
                for (int j = 0; j < mid; j++) {
                    if (queries[j][0] <= i && i <= queries[j][1])
                        dp |= (dp << queries[j][2]);
                }
                if (!dp[nums[i]]) {
                    valid = false;
                    break;
                }
            }
            if (valid)
                hi = mid;
            else
                lo = mid + 1;
        }
        return (lo <= q ? lo : -1);
    }
};

