class Solution {
    public:
    int dfs(vector<vector<int>>& dp, vector<int>& nums, vector<vector<int>>& queries, int& origInd, int ind, int cur){
        if(ind == queries.size()){
            if(cur == 0) {
                return ind;
            }
            return INT_MAX;
        }
        if(cur == 0){
            return ind;
        }
        if(cur < 0) return INT_MAX;
        if(dp[ind][cur] > -1) return dp[ind][cur];

        //take don't take
        int out = INT_MAX;
        if(queries[ind][0] <= origInd && queries[ind][1] >= origInd){
            out = dfs(dp, nums, queries, origInd, ind + 1, cur - queries[ind][2]);
        }
        out = min(out, dfs(dp, nums, queries, origInd, ind + 1, cur));
        dp[ind][cur] = out;
        return out;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        //should we eval each ind separately and find the earliest?

        //so then dp for each ind separately? 
        //take don't take 
        int minInd = -1;
        for(int i = 0; i < nums.size(); i++){
            vector<vector<int>> dp(queries.size(), vector<int>(nums[i] + 1, -1));
            int minQueries = dfs(dp, nums, queries, i, 0, nums[i]);

            minInd = max(minInd, minQueries);
        }
        return minInd == INT_MAX ? -1 : minInd;
    }
};

