class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& Q) {
        int qn = Q.size(), n = nums.size(), res = -1;
        vector<vector<int>> dp(n, vector<int>(1001, qn));

        for (int i = 0; i < n; i++) dp[i][0] = 0;

        for (int qi = 0; qi < qn; qi++) {
            auto &q = Q[qi];
            for (int i = q[0]; i <= q[1]; i++) {
                for (int d = 1000; d >= q[2]; d--) {
                    if (dp[i][d - q[2]] == qn) continue;
                    dp[i][d] = min(dp[i][d], max(dp[i][d - q[2]], qi));
                }
            }
        }

        for (int i = 0; i  < n; i++) {
            if (nums[i] == 0) continue;
            if (dp[i][nums[i]] == qn) return -1;
            res = max(res, dp[i][nums[i]]);
        }

        return res + 1;
    }
};

