class Solution {
    int st[1001][1001];
public:
    bool IsZeroArray(const vector<int>& nums)
    {
        for(auto x:nums)
            if(x > 0)
                return false;

        return true;
    }

    int solve(int idx, int val, const vector<int>& arr, const int m)
    {
        if(val == 0)
            return 0;

        if(idx >= m)
            return inf;

        if(st[idx][val] != -1)
            return st[idx][val];

        int ans = solve(idx+1, val, arr, m)+1;
        if(val >= arr[idx])
            ans = MIN(ans, solve(idx+1, val-arr[idx], arr, m)+1);

        return st[idx][val] = ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();
        int m = queries.size();

        vector<vector<int> > arr(n, vector<int>(m, inf));

        for(int i=0;i<m;i++)
        {
            int l,r,v;
            l = queries[i][0];
            r = queries[i][1];
            v = queries[i][2];

            for(int j=l;j<=r;j++)
                arr[j][i] = v;
        }

        /*
        for(int i=0;i<n;i++)
        {
            cout<<i<<" : ";
            for(int j=0;j<m;j++)
                cout<<arr[i][j]<<" ";
            cout<<"n";
        }*/

        int ans = -1;
        for(int i=0;i<n;i++)
        {
            memset(st, -1, sizeof(st));
            int idx = solve(0, nums[i], arr[i], m);
            if(idx >= inf)
            {
                ans = -1;
                break;
            }

            ans = MAX(ans, idx);
        }


        return ans;
    }
};

