class Solution:
    def subset_sum(self, nums, target):
        dp = [False] * (target + 1)
        dp[0] = True

        for num in nums:
            for j in range(target, num - 1, -1):
                dp[j] |= dp[j - num]
        return dp[target]

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        delta = [[] for _ in range(len(nums))]
        curDelta = []
        curQ = 0

        for i, val in enumerate(nums):
            for delt in delta[i]:
                if delt > 0:
                    curDelta.append(delt)
                else:
                    curDelta.remove(-delt)
            if self.subset_sum(curDelta, val):
                continue

            while curQ < len(queries) and not self.subset_sum(curDelta, val):
                start, end, value = queries[curQ]

                delta[start].append(value)
                if end + 1 < len(nums):
                    delta[end + 1].append(-value)

                if i >= start and i < end + 1:
                    curDelta.append(value)
                curQ += 1
            if curQ >= len(queries) and not self.subset_sum(curDelta, val):
                return -1
        return curQ

