class Solution {
public:
    bool DP(vector<vector<int>>& queries, int bound, int val, int idx){
        vector<int> reached (val + 1, 0);
        for(int i = 0; i <= bound; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int decr = queries[i][2];
            if(l <= idx and idx <= r){
                for(int j = (int)reached.size() - 1; j >= 0; j--){
                    if(reached[j] == 1 and j + decr < reached.size()) reached[j + decr] = 1;
                }
                if(decr < reached.size()) reached[decr] = 1;

                // cout << "inserted " << decr << endl;
                // cout << "reached" << endl;
                // for(int x : reached) cout << x << ' ';
                // cout << endl;

                if(reached.back() == 1) return true;
            }
        }
        return false;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // binary search of DP subroutines
        int ans = -1;
        for(int i = 0; i < nums.size(); i++){
            int val = nums[i];
            // binary search on queries
            if(nums[i] == 0){ // already zero
                ans = max(ans, 0);
                continue;
            }
            if(DP(queries, (int)queries.size() - 1, val, i) == false) return -1; // impossible to bring nums[i] to 0 using the queries available
            if(DP(queries, 0, val, i) == true){ // just the first query is enough to bring nums[i] to 0
                ans = max(ans, 1);
                continue;
            }
            int lo = 0;
            int hi = (int)queries.size() - 1;
            while(true){
                int mid = (lo + hi) / 2;
                bool ok = DP(queries, mid, val, i);
                bool ok_1 = DP(queries, mid + 1, val, i);
                if(not ok and ok_1){
                    ans = max(ans, mid + 2); // instead of mid + 1, since we are not returning index, but number of queries
                    break;
                }else if(ok) hi = mid;
                else lo = mid + 1;
                // cout << "lo: " << lo << " mid: " << mid << " hi: " << hi << " queries.size() " << queries.size() << endl;
            }
        }
        return ans;
    }
};

