class Solution {
public:
    bool canmakezero(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> cur(n + 1);

        for (int i = 0; i < k; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];
            cur[l].push_back(v);
            cur[r + 1].push_back(-v); 
        }

        unordered_map<int, int> count; 
        vector<int> com; 
        for (int i = 0; i < n; i++) {
            for (int x : cur[i]) {
                if (x < 0) {
                    if (count[-x] > 0) count[-x]--;
                } else {
                    count[x]++;
                }
            }
            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;
            for (auto [num, freq] : count) {
                for (int f = 0; f < freq; f++) {
                    for (int sum = nums[i]; sum >= num; sum--) {
                        dp[sum] = dp[sum] || dp[sum - num];
                    }
                }
            }

            if (!dp[nums[i]]) return false;
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size(), ans = -1;
        if (!canmakezero(nums, queries, r)) return -1;

        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (canmakezero(nums, queries, mid)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans;
    }
};

