class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(num <= 0 for num in nums):
            return 0

        possibleNums = defaultdict(set)
        for i, num in enumerate(nums):
            possibleNums[i].add(num)

        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                temp = possibleNums[j].copy()
                for num in temp:
                    if num - val >= 0:
                        possibleNums[j].add(num - val)

            if all(0 in possibleNums[i] for i, num in enumerate(nums)):
                return i + 1
        return -1

