class Solution {
  public int minZeroArray(int[] nums, int[][] queries) {
    boolean zeros = true;
    for (int n : nums) {
      if (n != 0) {
        zeros = false;
        break;
      }
    }
    if (zeros) {
      return 0;
    }
    int n = nums.length, m = queries.length;
    List<int[]>[] diff = new ArrayList[n];
    for (int i = 0; i < n; i++) {
      diff[i] = new ArrayList<>();
    }
    for (int i = 0; i < m; i++) {
      int l = queries[i][0], r = queries[i][1], v = queries[i][2];
      for (int j = l; j <= r; j++) {
        diff[j].add(new int[]{i, v});
      }
    }
    int l = 1, h = m, ret = -1;
    while (l <= h) {
      int mid = l + (h - l) / 2;
      boolean valid = true;
      for (int j = 0; j < n; j++) {
        int temp = nums[j];
        if (nums[j] == 0) {
          continue;
        }
        List<Integer> curr = new ArrayList<>();
        for (int[] currDiff : diff[j]) {
          if (currDiff[0] < mid) {
            curr.add(currDiff[1]);
          }
        }
        if (!validSub(curr, temp)) {
          valid = false;
          break;
        }
      }
      if (valid) {
        ret = mid;
        h = mid - 1;
      }
      else {
        l = mid + 1;
      }
    }
    return ret;

  }

  private boolean validSub(List<Integer> curr, int temp) {
    boolean[] dp = new boolean[temp + 1];
    dp[0] = true;
    for (int i = 0; i < curr.size(); i++) {
      int num = curr.get(i);
      for (int j = temp; j >= num; j--) {
        if (dp[j - num]) {
          dp[j] = true; 
        }
      }
    }
    return dp[temp];
  }
}

