class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int low=0,high=m,ans=-1;
        auto rec = [&](int i, vi &temp, int t, vvii &dp, auto &&self)->bool{
            if(t==0) return true;
            if(i==temp.size()) return false;
            if(dp[i][t] != -1) return dp[i][t];
            bool ans = false;
            if(t >= temp[i]) ans = ans | self(i+1, temp, t-temp[i], dp, self);
            if(ans) return dp[i][t] = true;
            ans = ans | self(i+1, temp, t, dp, self); 
            if(ans) return dp[i][t] = true;
            return dp[i][t] = ans;
        };
        auto check = [&](int q) {
            unordered_map<int,vi> mp;
            for(int i=0;i<q;i++) {
                int l=queries[i][0], r = queries[i][1], val = queries[i][2];
                rep(k,l,r+1) {
                    mp[k].pb(val);
                }
            }
            rep(i,0,n)  {
                vvii dp(mp[i].size(),vi(nums[i]+1,-1));
                if(!rec(0,mp[i],nums[i],dp,rec)) return false;
            }
            return true;
        };
        while(low<=high) {
            int mid = (low + high)>>1;
            if(check(mid)) {
                ans = mid;
                high = mid-1;
            } else {
                low = mid+1;
            }
        }
        return ans;
    }
};

