        int n = nums.length;
        int maxVal = 1001;

        boolean[][] dp = new boolean[n][maxVal];

        for(int i=0; i < n; i++)
            dp[i][0] = true;

        boolean flag = true;
        for(int j=0; j < n; j++){
            if(nums[j] != 0){
                flag = false;
                break;
            }
        }
        if(flag)
            return 0;

        for(int k=0; k < queries.length; k++){
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for(int j=l; j <= r; j++){
                for(int val=maxVal - 1; val >= v; val--){
                    if(dp[j][val - v])
                        dp[j][val] = true;
                }
            }

            boolean temp = true;
            for(int i=0; i < n; i++){
                if(!dp[i][nums[i]]) {
                    temp = false;
                    break;
                }
            }

            if(temp)
                return k + 1;
        }

        return -1;
    }
}

