                    }
                }
                if (sum < nums[i] || !sum(count, nums[i], 10)) left = mid+1;
                else right = mid-1;
            }
            if (left == queries.length) return -1;
            else min = Math.max(min, left+1);
        }
        return min;
    }
    private boolean sum(int[] count, int targ, int n) {
        if (targ == 0) return true;
        if (n <= 0) return false;
        for (int i=Math.min(count[n], targ/n); i>=0; i--) {
            if (sum(count, targ - i*n, n-1)) return true;
        }
        return false;
    }
}

