function minZeroArray(nums: number[], queries: number[][]): number {
     const n = nums.length;
    const elementQueries: { queryIndex: number; val: number }[][] = Array.from({ length: n }, () => []);

    for (let j = 0; j < queries.length; j++) {
        const [l, r, val] = queries[j];
        for (let i = l; i <= r; i++) {
            elementQueries[i].push({ queryIndex: j, val });
        }
    }

    let maxK = 0;

    for (let i = 0; i < n; i++) {
        const s = nums[i];
        if (s === 0) {
            continue;
        }

        const qList = elementQueries[i];
        const sumMap = new Map<number, number>();
        sumMap.set(0, -1);

        let found = false;

        for (const q of qList) {
            const currentVal = q.val;
            const currentQueryIndex = q.queryIndex;

            const currentSums = Array.from(sumMap.entries());
            for (const [sum, earliestJ] of currentSums) {
                const newSum = sum + currentVal;
                if (newSum > s) continue;

                if (!sumMap.has(newSum) || currentQueryIndex < sumMap.get(newSum)!) {
                    sumMap.set(newSum, currentQueryIndex);
                }

                if (newSum === s) {
                    const candidateK = currentQueryIndex + 1;
                    if (candidateK > maxK) {
                        maxK = candidateK;
                    }
                    found = true;
                    break;
                }
            }
            if (found) {
                break; // Break outer loop for queries
            }
        }

        if (!found) {
            return -1;
        }
    }

    if (maxK > queries.length) {
        return -1;
    }

    return maxK;
};

