class Solution {
public:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<bool>> possible(n);

        for (int j = 0; j < n; j++) {
            possible[j].resize(nums[j] + 1, false);
            possible[j][0] = true;
        }

        for (int i = 0; i < k; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for (int j = l; j <= r; j++) {
                vector<bool> new_possible = possible[j];
                for (int s = 0; s <= nums[j]; s++) {
                    if (possible[j][s] && s + val <= nums[j]) {
                        new_possible[s + val] = true;
                    }
                }
                possible[j].swap(new_possible);
            }
        }

        for (int j = 0; j < n; j++) {
            if (!possible[j][nums[j]]) {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int Q = queries.size();

        bool allZero = all_of(nums.begin(), nums.end(), [](int x) { return x == 0; });
        if (allZero) return 0;

        int low = 1, high = Q, ans = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (check(nums, queries, mid)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return ans;
    }
};

