class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = queries.length;
        if (pass(nums, 0, queries))
        	return 0;
        if (!pass(nums, n, queries))
        	return -1;
        int left = 0;
        int right = n;
        while (left + 1 < right) {
        	int mid = (left + right) / 2;
        	if (pass(nums, mid, queries))
        		right = mid;
        	else
        		left = mid;
        }
        return right;
    }

	private static boolean pass(int[] nums, int index, int[][] queries) {
		int n = nums.length;
		int[][] vals = new int[n][11];
		for (int i = 0; i < index; i++) {
			for (int j = queries[i][0]; j <= queries[i][1]; j++)
				vals[j][queries[i][2]]++;
		}
		for (int i = 0; i < n; i++) {
			if (!canMake(nums[i], vals[i]))
				return false;
		}
		return true;
	}

	private static boolean canMake(int k, int[] vals) {
		if (k == 0)
			return true;
		if (k < 0)
			return false;
		Set<Integer> set = new HashSet();
		for (int i = 10; i > 0; i--) {
			if (vals[i] == 0)
				continue;
			Set<Integer> now = new HashSet();
			now.addAll(set);
			for (int j = 1; j <= vals[i]; j++) {
				if (k < i * j)
					break;
				now.add(i * j);
				for (int prev : set) {
					if (prev + i * j <= k)
						now.add(prev + i * j);
				}
				if (now.contains(k))
					return true;
			}
			set = now;
		}
		return false;
    }
}

