class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        p = {i: [] for i in range(len(nums))}

        for i, q in enumerate(queries):
            l, r, v = q
            for j in range(l, r + 1):
                p[j].append((v, i + 1))

        def check(val, arr):
            if val == 0:
                return 0

            @cache
            def f(v, i):
                if i == len(arr):
                    return -1

                if arr[i][0] == v:
                    return arr[i][1]

                take = -1
                skip = f(v, i + 1)

                if arr[i][0] < v:
                    take = f(v - arr[i][0], i + 1)

                if take == -1:
                    return skip

                if skip == -1:
                    return take

                return min(skip, take)

            return f(val, 0)

        values = []
        for i, n in enumerate(nums):
            values.append(check(n, p[i]))

        if min(values) == -1:
            return -1

        return max(values)

