class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        q = len(queries)
        coin_lists = [[] for _ in range(n)]
        for qi, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                coin_lists[j].append((qi, v))
        for j in range(n):
            coin_lists[j].sort(key=lambda x: x[1])
        def can_form_all(k: int) -> bool:
            for j in range(n):
                target = nums[j]
                if target == 0:
                    continue
                available = [v for qi, v in coin_lists[j] if qi < k]
                dp = 1
                for coin in available:
                    dp |= dp << coin
                    if dp >> target & 1:
                        break
                if not (dp >> target) & 1:
                    return False
            return True
        low, high = 0, q + 1
        ans = -1
        while low < high:
            mid = (low + high) // 2
            if can_form_all(mid):
                ans = mid
                high = mid
            else:
                low = mid + 1        
        return ans if ans != -1 and ans <= q else -1

