class Solution {
    boolean solve(int i, int q, int idx, int mid, int[][] queries, Boolean dp[][]){
        if(i == 0){
            return true;
        }
        if(q == mid+1){
            return false;
        }
        if(dp[i][q] != null){
            return dp[i][q];
        }
        dp[i][q] = false;
        if(queries[q][0] <= idx && queries[q][1] >= idx && i >= queries[q][2]){
            dp[i][q] |= solve(i-queries[q][2], q+1, idx, mid, queries, dp);
        }
        dp[i][q] |= solve(i, q+1, idx, mid, queries, dp);
        return dp[i][q];
    } 
    boolean isZero(int mid, int nums[], int[][] queries){
        for(int i=0; i<nums.length; i++){
            if(!solve(nums[i], 0, i, mid, queries, new Boolean[nums[i]+1][mid+1])){
                return false;
            }
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int low = 0;
        int high = queries.length-1;
        int ans = -1;
        int nonZeros = 0;
        for(int i:nums){
            if(i > 0){
                nonZeros++;
            }
        }
        if(nonZeros == 0){
            return 0;
        }
        while(low <= high){
            int mid = (low+high)/2;
            if(isZero(mid, nums.clone(), queries)){
                ans = mid;
                high = mid-1;
            }else low = mid+1;
        }
        if(ans == -1){
            return ans;
        }
        return ans+1;
    }
}

